        self.shenqi_info = {
            6200: {"name": "轩辕剑", "desc": "黄帝采首山之铜铸此剑，以天文古字铭之，蕴腾龙之魂，光若屈阳之华，寄天地之神，气有顺天之义。#r【法宝等级】 神器"},
            6201: {"name": "华光玉", "desc": "昔时，女娲补天于高山之巅，遗五色之玉，润而有光，华彩滟滟，有修复万物之能。#r【法宝等级】 神器"},
            6202: {"name": "墨魂笔", "desc": "挥毫洒落，探玄为奇，墨骨为魂，浓淡由他。此笔蕴浩然之气，绘万物，赋其魂。#r【法宝等级】 神器"},
            6203: {"name": "独弦琴", "desc": "凤栖于桐，鸣而为歌。伏羲削此桐为琴，其形法天象地，曾有七弦，作宫商角徵之音，可动喜怒悲欣之情。#r【法宝等级】 神器"},
            6204: {"name": "噬魂齿", "desc": "昆仑之渊有异兽，顽嚚不友，好行凶德。陆吾杀之，禁其魂于其齿，锐不可当。#r【法宝等级】 神器"},
            6205: {"name": "泪痕碗", "desc": "忽梦少年时，不语泪潸然。瑾瑜之玉为盏，嵌鲛珠，可贮世间女儿之泪。#r【法宝等级】 神器"},
            6206: {"name": "黄金甲", "desc": "黄金甲胄，三界至坚者。披此金甲者，刀枪不入，志固而愈勇。#r【法宝等级】 神器"},
            6207: {"name": "月光草", "desc": "神木之巅有草，其影亭亭，翩然有凌波之态，其叶葳蕤，皎皎若月华之色。此草乃句芒手植，蕴皓月清辉，可治百病。#r【法宝等级】 神器"},
            6208: {"name": "四神鼎", "desc": "禺强以鼎为祀，祭天地，祷众生，纳四海怨怼，佑三界平靖。然恶业无尽，此鼎化为至阴至煞之器。#r【法宝等级】 神器"},
            6209: {"name": "明火珠", "desc": "煌煌天火，巨焰如帜。祝融以气化珠，珠内藏天火之焰，焚尽魍魉魑魅，明光可照天地。#r【法宝等级】 神器"},
            6210: {"name": "昆仑镜", "desc": "镜者，景也，风后登昆仑之丘，取山中金石以为镜，望而无尽，隐而见影，有影照回忆之能。#r【法宝等级】 神器"},
            6211: {"name": "清泽谱", "desc": "沧浪激石，泠泠作响，声成韵，录其谱。应龙乘风来去，云雾滃然而从，啸而为歌，兴风雨而净万物。#r【法宝等级】 神器"},
            6212: {"name": "星斗盘", "desc": "星移斗转，瀚海灿灿，天而为相，地则成形。颛顼以星斗为棋，司生司杀。星斗之盘，可参天机。#r【法宝等级】 神器"},
            6213: {"name": "天罡印", "desc": "风翻云起，震荡乾坤，天罡一出，四海宾服。蚩尤行兵所持之印，王霸之气俨然，有号令天下之能。#r【法宝等级】 神器"},
            6214: {"name": "玲珑结", "desc": "一丝一扣系相思，玲珑心意知不知？嫘祖亲桑，抽丝编绢成此结，缠绵情意，令人心驰神往，于不觉间掌控人心。#r【法宝等级】 神器"},
            6215: {"name": "轩辕剑", "desc": "黄帝采首山之铜铸此剑，以天文古字铭之，蕴腾龙之魂，光若屈阳之华，寄天地之神，气有顺天之义。#r【法宝等级】 神器"},
            6216: {"name": "华光玉", "desc": "昔时，女娲补天于高山之巅，遗五色之玉，润而有光，华彩滟滟，有修复万物之能。#r【法宝等级】 神器"},
            6217: {"name": "墨魂笔", "desc": "挥毫洒落，探玄为奇，墨骨为魂，浓淡由他。此笔蕴浩然之气，绘万物，赋其魂。#r【法宝等级】 神器"},
            6218: {"name": "独弦琴", "desc": "凤栖于桐，鸣而为歌。伏羲削此桐为琴，其形法天象地，曾有七弦，作宫商角徵之音，可动喜怒悲欣之情。#r【法宝等级】 神器"},
            6219: {"name": "噬魂齿", "desc": "昆仑之渊有异兽，顽嚚不友，好行凶德。陆吾杀之，禁其魂于其齿，锐不可当。#r【法宝等级】 神器"},
            6220: {"name": "泪痕碗", "desc": "忽梦少年时，不语泪潸然。瑾瑜之玉为盏，嵌鲛珠，可贮世间女儿之泪。#r【法宝等级】 神器"},
            6221: {"name": "黄金甲", "desc": "黄金甲胄，三界至坚者。披此金甲者，刀枪不入，志固而愈勇。#r【法宝等级】 神器"},
            6222: {"name": "月光草", "desc": "神木之巅有草，其影亭亭，翩然有凌波之态，其叶葳蕤，皎皎若月华之色。此草乃句芒手植，蕴皓月清辉，可治百病。#r【法宝等级】 神器"},
            6223: {"name": "四神鼎", "desc": "禺强以鼎为祀，祭天地，祷众生，纳四海怨怼，佑三界平靖。然恶业无尽，此鼎化为至阴至煞之器。#r【法宝等级】 神器"},
            6224: {"name": "明火珠", "desc": "煌煌天火，巨焰如帜。祝融以气化珠，珠内藏天火之焰，焚尽魍魉魑魅，明光可照天地。#r【法宝等级】 神器"},
            6225: {"name": "昆仑镜", "desc": "镜者，景也，风后登昆仑之丘，取山中金石以为镜，望而无尽，隐而见影，有影照回忆之能。#r【法宝等级】 神器"},
            6226: {"name": "清泽谱", "desc": "沧浪激石，泠泠作响，声成韵，录其谱。应龙乘风来去，云雾滃然而从，啸而为歌，兴风雨而净万物。#r【法宝等级】 神器"},
            6227: {"name": "星斗盘", "desc": "星移斗转，瀚海灿灿，天而为相，地则成形。颛顼以星斗为棋，司生司杀。星斗之盘，可参天机。#r【法宝等级】 神器"},
            6228: {"name": "天罡印", "desc": "风翻云起，震荡乾坤，天罡一出，四海宾服。蚩尤行兵所持之印，王霸之气俨然，有号令天下之能。#r【法宝等级】 神器"},
            6229: {"name": "玲珑结", "desc": "一丝一扣系相思，玲珑心意知不知？嫘祖亲桑，抽丝编绢成此结，缠绵情意，令人心驰神往，于不觉间掌控人心。#r【法宝等级】 神器"},
            6230: {"name": "莫愁铃", "desc": "水中出藻玉，钟灵而生，多文彩。藻玉琢磨为铃，名莫愁。此铃随风而鸣，醒梦魇，再无忧。#r【法宝等级】 神器"},
            6231: {"name": "天机城", "desc": "黄帝采首山之铜铸此剑，以天文古字铭之，蕴腾龙之魂，光若屈阳之华，寄天地之神，气有顺天之义。#r【法宝等级】 神器"},
            6233: {"name": "莫愁铃", "desc": "水中出藻玉，钟灵而生，多文彩。藻玉琢磨为铃，名莫愁。此铃随风而鸣，醒梦魇，再无忧。#r【法宝等级】 神器"},
            6234: {"name": "天机城", "desc": "黄帝采首山之铜铸此剑，以天文古字铭之，蕴腾龙之魂，光若屈阳之华，寄天地之神，气有顺天之义。#r【法宝等级】 神器"},
            6232: {"name": "鸿蒙石", "desc": "盘古破鸿蒙，清浊之气分化，乃成鸿蒙石。千万年来，此石蕴藉天地灵气，终孕生一九窍石猴。得此石者，可尽收天地灵明之能。#r【法宝等级】 神器"},
            6235: {"name": "鸿蒙石", "desc": "盘古破鸿蒙，清浊之气分化，乃成鸿蒙石。千万年来，此石蕴藉天地灵气，终孕生一九窍石猴。得此石者，可尽收天地灵明之能。#r【法宝等级】 神器"}
        }
