    def tryDecay(self,keyArr, val ,pet):

        if (type(keyArr) == 'str'):
            keyArr = [keyArr]
        for key in keyArr:
            if (key in pet):
                pet[key] = pet[key] - val if pet[key] - val > 0 else 0


    def fixMax(self,key, maxKeys,pet):
        if (key in pet):
            for mk in maxKeys:
                if (mk in pet) :
                    pet[key] = pet[key] if pet[key] > pet[mk] else pet[mk]
                    return


    def fix_pet_decay_attr(self, pet, type, downzz):
        grade = +(pet['pet_grade'] if pet['pet_grade'] else 0)
        growth = (pet['growth'] if pet['growth'] else pet['cheng_zhang'] if pet['cheng_zhang'] else 0) * 1000
        if (grade== None or growth==None) :
            return
        for type in range(0, 5):
            if type == 0:
                decay = math.ceil(downzz * grade * 2 / 1000 * (700 + growth / 2) / 1000 * 4 / 3)
                self.tryDecay('attack', decay,pet)
                break
            if type == 1:
                decay = math.ceil(downzz * grade * 7 / 4000 * (700 + growth / 2) / 1000)
                self.tryDecay('defence', decay,pet)
                break
            if type == 2:
                speed = pet['smartness'] if pet['smartness'] else pet['min_jie']
                if (speed != 0):
                    decay = math.ceil(downzz * speed / 1000)
                    self.tryDecay('speed', decay,pet)
                break
            if type == 4:
                decay = math.ceil(downzz * grade / 1000)
                keys = ['max_blood', 'blood_max']
                self.tryDecay(keys, decay,pet)
                self.fixMax('blood', keys,pet)
                break
            if type == 5:
                decayMp = math.ceil(downzz * grade / 500)
                mpKeys = ['max_magic', 'magic_max']
                self.tryDecay(mpKeys, decayMp,pet)
                self.fixMax('magic', mpKeys,pet)
                decayLingli = math.ceil(downzz * 3 / 10 * grade / 1000)
                self.tryDecay(['wakan', 'ling_li'], decayLingli,pet)
                break


